require 'json'
p_sup = JSON.parse(power_suppliers_json)

states = []
p_sup.each do |entry|
  states.push entry['state'] 
end

states = states.uniq

states.each do |state|
  util_list = []
  p_sup.each do |entry|
    util_list.push entry['utility_name'] if state == entry['state']
  end
  util_list = util_list.map {|name| "\"#{name}\""}
  command =  "power_suppliers:#{state} #{util_list.join(" ")}"
  command = command.gsub('"','\"')
  command = "echo \"lpush #{command}\" | redis-cli"
  system command
end
